import{_ as a,c as r,a2 as t,o as i}from"./chunks/framework.CU8mY_KO.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{"search":true},"headers":[],"relativePath":"docs/A/A.md","filePath":"docs/A/A.md"}'),E={name:"docs/A/A.md"};function n(o,e,d,l,A,f){return i(),r("div",null,e[0]||(e[0]=[t('<h2 id="阿" tabindex="-1">阿 <a class="header-anchor" href="#阿" aria-label="Permalink to &quot;阿&quot;">​</a></h2><p>(ā)：① ē。大山。《<a href="https://baike.baidu.com/item/%E5%B0%8F%E9%9B%85%C2%B7%E8%8F%81%E8%8F%81%E8%80%85%E8%8E%AA/1951151?fr=aladdin" target="_blank" rel="noreferrer">诗经·小雅·菁菁者莪</a>》：“菁菁者莪,在彼中~。”《<a href="https://baike.baidu.com/item/%E6%BB%95%E7%8E%8B%E9%98%81%E5%BA%8F/480325?fr=aladdin" target="_blank" rel="noreferrer">滕王阁序-王勃</a>》：“访风景于崇~。（崇：高。）”➁ ē。 山、水的转弯处。《<a href="https://baike.baidu.com/item/%E4%B9%9D%E6%AD%8C%C2%B7%E5%B1%B1%E9%AC%BC/9893820?fr=aladdin" target="_blank" rel="noreferrer">九歌·山鬼-屈原</a>》：“若有人兮山之~。”《<a href="https://baike.baidu.com/item/%E7%A9%86%E5%A4%A9%E5%AD%90%E4%BC%A0/681117?fr=aladdin" target="_blank" rel="noreferrer">穆天子传</a>》卷一：“天子饮于河水之~。”（河：指黄河。）➂ ē。屋檐。《<a href="https://baike.baidu.com/item/%E8%A5%BF%E5%8C%97%E6%9C%89%E9%AB%98%E6%A5%BC/10407065?fromtitle=%E5%8F%A4%E8%AF%97%E5%8D%81%E4%B9%9D%E9%A6%96%C2%B7%E8%A5%BF%E5%8C%97%E6%9C%89%E9%AB%98%E6%A5%BC&amp;fromid=1344002&amp;fr=aladdin" target="_blank" rel="noreferrer">古诗十九首·西北有高楼</a>》：“~阁三重阶。”（阁：楼阁。阶：台阶。）➃ ē。偏袒，迎合。《<a href="https://baike.baidu.com/item/%E9%9F%A9%E9%9D%9E%E5%AD%90%C2%B7%E6%9C%89%E5%BA%A6/19831199?fr=aladdin" target="_blank" rel="noreferrer">韩非子·有度</a>》：“法不~贵。”（贵：指有权势的人。）成语有“刚直不阿、阿谀逢迎”。➄ 名词词头。多用于亲属名称或人名的前面。《<a href="https://baike.baidu.com/item/%E6%9C%A8%E5%85%B0%E8%AF%97/1307816?fr=aladdin" target="_blank" rel="noreferrer">木兰诗</a>》：“~姊闻妹来。《<a href="https://baike.baidu.com/item/%E4%B8%89%E5%9B%BD%E5%BF%97%C2%B7%E9%AD%8F%E4%B9%A6%C2%B7%E6%AD%A6%E5%B8%9D%E7%BA%AA/14697473" target="_blank" rel="noreferrer">三国志·魏书·武帝纪</a>》斐注引《<a href="https://baike.baidu.com/item/%E6%9B%B9%E7%9E%92%E4%BC%A0/4856624?fr=aladdin" target="_blank" rel="noreferrer">曹瞒传</a>》：“太祖一名吉利，小字~瞒。” ➅ hē（呵）。通“呵”，斥责。《<a href="https://baike.baidu.com/item/%E8%80%81%E5%AD%90/5448?fr=aladdin" target="_blank" rel="noreferrer">老子</a>》：“唯之与~，相去几何？”</p><h2 id="哀" tabindex="-1">哀 <a class="header-anchor" href="#哀" aria-label="Permalink to &quot;哀&quot;">​</a></h2><p>(āi)：① 悲痛，伤心。《<a href="https://baike.baidu.com/item/%E7%A6%BB%E9%AA%9A/1045?fr=aladdin" target="_blank" rel="noreferrer">离骚-屈原</a>》：“~众芳之芜秽（wú huì）。”（芳：香草。芜秽：荒芜。）㊕ 父母之丧。《<a href="https://baike.baidu.com/item/%E5%AE%8B%E4%B9%A6/1995398?fr=aladdin" target="_blank" rel="noreferrer">宋书·张敷传</a>》：“居~毁灭，孝道淳至。”➁ 怜悯，同情。《<a href="https://baike.baidu.com/item/%E9%9F%A9%E9%9D%9E%E5%AD%90%C2%B7%E7%94%A8%E4%BA%BA/19828835" target="_blank" rel="noreferrer">韩非子·用人</a>》：“忧悲不~怜。”【辩】哀，戚，悲，悼。四字都有悲伤的意思。但“戚”字一般是表示忧苦、悲哀；“哀”与“悲”有怜悯、同情的意思，“哀”的感情色彩要重些；“悼”则是“悲痛”的意思，多用于对死者表示沉痛悼念。</p>',4)]))}const h=a(E,[["render",n]]);export{b as __pageData,h as default};
