import{aw as e,y as r,z as t,b0 as p}from"./chunks/framework.lgr6cdJL.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{"search":true},"headers":[],"relativePath":"docs/C/cai.md","filePath":"docs/C/cai.md"}'),i={name:"docs/C/cai.md"};function l(o,a,s,c,n,h){return t(),r("div",null,a[0]||(a[0]=[p('<h2 id="猜" tabindex="-1">猜 <a class="header-anchor" href="#猜" aria-label="Permalink to &quot;猜&quot;">​</a></h2><p>（cāi）:</p><p>怀疑。《<a href="https://baike.baidu.com/item/%E5%B7%A6%E4%BC%A0/371757?fr=ge_ala" target="_blank" rel="noreferrer">左传·昭公七年</a>》：“虽吾子亦有～焉。”《<a href="https://baike.baidu.com/item/%E5%90%8E%E6%B1%89%E4%B9%A6/1949780?fr=ge_ala" target="_blank" rel="noreferrer">后汉书·申屠刚传</a>》：“平帝时，王莽专政，朝多～忌。”【注意】古代汉语中，“猜”字不当“猜测”讲。</p><h2 id="才1" tabindex="-1">才<sup>1</sup> <a class="header-anchor" href="#才1" aria-label="Permalink to &quot;才&lt;sup&gt;1&lt;/sup&gt;&quot;">​</a></h2><p>（cái）:</p><p>➀ 才能。《<a href="https://baike.baidu.com/item/%E8%AE%BA%E8%AF%AD/372830" target="_blank" rel="noreferrer">论语·泰伯</a>》：“如有周公之～之美，使骄且吝，其余不足观也。”成语有“才疏学浅”、“才轻德薄”。又人才，有才能得人。曹操《<a href="https://baike.baidu.com/item/%E6%B1%82%E8%B4%A4%E4%BB%A4/14893532?fr=ge_ala" target="_blank" rel="noreferrer">求贤令</a>》：“唯～是举，吾得而用之。”（唯才是举：推举有才能的人。）</p><p>➁ 副词。刚刚，仅仅。《<a href="https://baike.baidu.com/item/%E6%99%8B%E4%B9%A6/781568?fr=ge_ala" target="_blank" rel="noreferrer">晋书·谢混传</a>》：“～小富贵，便豫人家事。”（豫：干预。）</p><p>➂ 通“裁”。裁决。《<a href="https://baike.baidu.com/item/%E6%88%98%E5%9B%BD%E7%AD%96/2690" target="_blank" rel="noreferrer">战国策·赵策一</a>》：“今有城市之邑（yì）七十，愿拜内（nà）之于王，唯王～之。”（内：通“纳”。接纳，交纳。）</p><h2 id="才2" tabindex="-1">才<sup>2</sup> <a class="header-anchor" href="#才2" aria-label="Permalink to &quot;才&lt;sup&gt;2&lt;/sup&gt;&quot;">​</a></h2><p>（纔（cái））:</p><p>➀ 副词。刚刚，方才。晁错《<a href="https://baike.baidu.com/item/%E8%AE%BA%E5%AE%88%E8%BE%B9%E7%96%8F/6371381?fr=ge_ala#2" target="_blank" rel="noreferrer">言守边备塞疏</a>》：“救之，少发则不足，多发，远县～至，则胡又已去。”（远县：指远县的军队。胡：指匈奴。）</p><p>➁ 副词。仅仅，只。 陶潜《<a href="https://baike.baidu.com/item/%E6%A1%83%E8%8A%B1%E6%BA%90%E8%AE%B0/105?fr=ge_ala" target="_blank" rel="noreferrer">桃花源记</a>》：“初极狭，～通人。”</p><h2 id="材" tabindex="-1">材 <a class="header-anchor" href="#材" aria-label="Permalink to &quot;材&quot;">​</a></h2><p>（cái）:</p><p>➀ 木材，木料。《韩非子·内储说上》：“～木尽则无以为守备。”（守备：指防备用的器物。）<span class="char-circle">引</span> 材料，原料。《管子·小问》：“致天下之精～，来天下之良工，则有战胜之器矣。”（致：使……到来。器：用具。）<span class="char-circle">特</span> 棺材。《陈书·周弘直传》：“气绝已后，便买市中见～，～必须小形者。”</p><p>➁ 通“才”。才能。《史记·淮南王刘安传》：“～干绝人。”柳宗元《答韦中立论师道书》：“仆～不足。”（仆：自称的谦词，相当于我。）<span class="char-circle">引</span> 有才能的。《韩非子·饰邪》：“奸臣愈进而～臣退，则主惑而不知所行。”（主：君主。惑：迷惑。）</p><p>➂ 通“裁”。成，成就。《荀子·富国》：“治万变，～万物，养万民。”</p><h2 id="财" tabindex="-1">财 <a class="header-anchor" href="#财" aria-label="Permalink to &quot;财&quot;">​</a></h2><p>（cái）:</p><p>➀</p><p>➁</p><p>➂</p><p>➃</p><p>➄</p><h2 id="裁" tabindex="-1">裁 <a class="header-anchor" href="#裁" aria-label="Permalink to &quot;裁&quot;">​</a></h2><p>（cái）:</p><p>➀</p><p>➁</p><p>➂</p><p>➃</p><p>➄</p><h2 id="采" tabindex="-1">采 <a class="header-anchor" href="#采" aria-label="Permalink to &quot;采&quot;">​</a></h2>',32)]))}const f=e(i,[["render",l]]);export{b as __pageData,f as default};
